mapper.update:
UPDATE TB_USER
SET 
     USER_NAME = #{userName},
     USER_NICKNAME = #{userNickname},
     USER_PHONE = #{userPhone}
WHERE USER_NO = #{userNo}

mapper.select:
SELECT IMAGE_URL
FROM TB_PROFILE_IMAGE
WHERE USER_NO = #{userNo}

mapper.update:
UPDATE TB_PROFILE_IMAGE
SET IMAGE_URL = #{imageUrl}
WHERE USER_NO = #{userNo}
